        Position storage p = positions[vault][positionIdOf[msg.sender][vault]];

        _stake(msg.sender, extraEquity);
        p.share += amountToShare(extraEquity);

        emit PositionChanged(
            msg.sender, 
            vault, 
            shareToAmount(p.share),
            p.share,
            debtAmountOf(msg.sender, vault)
        ); 
    }


