// SPDX-License-Identifier: MIT
pragma solidity ^0.8.3;

interface IInterestModel {
    function calcInterestRate(uint256 debt, uint256 floating)
        external
        pure
        returns (uint256);
}

contracts/interface/ISwapHelper.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.4;

/************************************************************
 * @dev
 * Glossary
 * X : Input Token
 * Y : Output Token
 * Dx : Amount of Input Token
 * Dy : Amount of Output Token
 *************************************************************/
interface ISwapHelper {

    function getDy(
        address tokenX,
        address tokenY,
        uint256 dx
    ) external view returns (uint256 dy);

    function getDx(
        address tokenX,
        address tokenY,
        uint256 dy
    ) external view returns (uint256 dx);

    function exchange(
        address tokenX,
        address tokenY,
        uint256 dx,
        uint256 minDy
    ) payable external returns (uint256 dy);
}

