contracts/mock/MockSwap.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.4;

import "../lib/interface/IERC20.sol";
import "../lib/utils/SafeToken.sol";


contract MockSwap {

    /**
        @dev      ,
        EvmoSwapRouter    DEX  .

        native token(EVMOS)      1: 2 .
        (EVMOS  =    * 2)

        TODO        .
        ex) IERC20(tokenAddress).mint(address(this), MAX_UINT / 2);
     */

    mapping(address => bool) public isSupported;

    constructor (
        address[] memory tokens
    ){
        for (uint256 i = 0; i < tokens.length; i++) {
            isSupported[tokens[i]] = true;
        }
    }

    function getDx(
        address tokenX,
        address tokenY,
        uint256 dy
    ) public pure returns (uint256) {
        if(tokenX == address(0))
            return dy / 2;
        else if(tokenY == address(0))
            return dy * 2;
        else
            return dy;
    }

    function getDy(
        address tokenX,
        address tokenY,
        uint256 dx
    ) public pure returns (uint256) {
        if(tokenX == address(0))
            return dx * 2;
        else if(tokenY == address(0))
            return dx / 2;
        else
            return dx;
    }

    function exchange(
        address tokenX,
        address tokenY,
        uint256 dx,
        uint256 /* minDy */
    ) public payable returns(uint256 dy) {
        if(tokenX == address(0)){
            require(msg.value == dx, "MockSwap: msg.value != dx");
        } else {
            SafeToken.safeTransferFrom(tokenX, msg.sender, address(this), dx);
        }

        dy = getDy(tokenX, tokenY, dx);
        if(tokenY == address(0)){
            SafeToken.safeTransferEVMOS(msg.sender, dy);
        } else {
            SafeToken.safeTransfer(tokenY, msg.sender, dy);
        }
    }

    /// @dev Fallback function to accept EVMOS.
    receive() external payable {}
}

