// SPDX-License-Identifier: MIT
pragma solidity ^0.8.3;
import "../interface/ISwapHelper.sol";
import "../interface/IEvmoSwapRouter.sol";
import "../lib/utils/SafeToken.sol";

contract EvmoSwapHelper is ISwapHelper {

    IEvmoSwapRouter public router;

    constructor (
        address router_
    ){
        router = IEvmoSwapRouter(payable(router_));
    }

    function getDy(
        address tokenX,
        address tokenY,
        uint256 dx
    ) public override view returns (uint256 dy) {
        address[] memory path = new address[](2);
        path[0] = tokenX == address(0) ? router.WETH() : tokenX;
        path[1] = tokenY == address(0) ? router.WETH() : tokenY;

        uint256[] memory amounts = router.getAmountsOut(dx, path);
        return amounts[1];
    }

    function getDx(
        address tokenX,
        address tokenY,
        uint256 dy
    ) public override view returns (uint256 dx) {
        address[] memory path = new address[](2);
        path[0] = tokenX == address(0) ? router.WETH() : tokenX;
        path[1] = tokenY == address(0) ? router.WETH() : tokenY;

        uint256[] memory amounts = router.getAmountsIn(dy, path);
        return amounts[0];
    }

    function exchange(
        address tokenX,
        address tokenY,
        uint256 dx,
        uint256 minDy
    ) payable public override returns (uint256) {
        address[] memory path = new address[](2);
        uint256[] memory amounts;

        // if tokenY == address(0) too, swapExactETHForTokens will be reverted.
        if(tokenX == address(0)){
            require(dx == msg.value, "exchange: invalid msg.value");
            path[0] = router.WETH();
            path[1] = tokenY;

            amounts = router.swapExactETHForTokens{value: dx}(
                minDy,
                path,
                msg.sender,
                block.timestamp + 600 // 10 minutes
            );

            return amounts[1];
        }
        /// @dev msg.sender should approve this helper contract first
        else {
            SafeToken.safeTransferFrom(
                tokenX,
                msg.sender,
                address(this),
                dx
            );

            SafeToken.safeApprove(tokenX, address(router), dx);

            path[0] = tokenX;

            if(tokenY == address(0)){
                path[1] = router.WETH();

                amounts = router.swapExactTokensForETH(
                    dx,
                    minDy,
                    path,
                    msg.sender,
                    block.timestamp + 600 // 10 minutes
                );
            }
            else {
                path[1] = tokenY;
                amounts = router.swapExactTokensForTokens(
                    dx,
                    minDy,
                    path,
                    msg.sender,
                    block.timestamp + 600 // 10 minutes
                );
            }

            return amounts[1];
        }
    }
}

contracts/interface/ISwapHelper.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.4;

/************************************************************
 * @dev
 * Glossary
 * X : Input Token
 * Y : Output Token
 * Dx : Amount of Input Token
 * Dy : Amount of Output Token
 *************************************************************/
interface ISwapHelper {

    function getDy(
        address tokenX,
        address tokenY,
        uint256 dx
    ) external view returns (uint256 dy);

    function getDx(
        address tokenX,
        address tokenY,
        uint256 dy
    ) external view returns (uint256 dx);

    function exchange(
        address tokenX,
        address tokenY,
        uint256 dx,
        uint256 minDy
    ) payable external returns (uint256 dy);
}

