contracts/UnbondedEvmos.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.3;

import 'hardhat/console.sol';
import './interface/IUnbondedEvmos.sol';
import './interface/IVault.sol';
import './lib/OwnableUpgradeable.sol';
import './lib/ERC20Upgradeable.sol';
import './lib/utils/SafeToken.sol';

contract UnbondedEvmos is IUnbondedEvmos, OwnableUpgradeable, ERC20Upgradeable {
    event Lock(address account, address vault, uint256 lockedIndex);
    event Unlock(address account, uint256 amount, uint256 returned);
    event Supply(uint256 amount);
    event Withdraw(address account, uint256 amount);
    event UpdateMinterStatus(address account, bool status);
    event UpdateConfigs(uint256 unbondingInterval);

    mapping(address => bool) public override isMinter;

    uint256 public override lastUnbondedAt;
    uint256 public override unbondingInterval; // maybe 14 + 2 days

    /// @dev unbondLimit?
    // uint256 public unbondLimit = 7;

    struct Locked {
        bool received;
        address account;
        address vault;
        uint256 amount;
        uint256 debtShare;
        uint256 unlockedAt;
    }

    Locked[] public locks;
    uint256 public locksLength;

    /** @dev
     * kor) [논의 필요] Locked[]를 길이가 7인 큐로 지정.
     lockedIds: locks 배열에 들어있는 Lock 객체의 array index
     accounts can request up to 7 unbonds for 14 days,
     just like when delegate EVMOS to Validator.
     */
    struct LockedQueue {
        uint256 nextUnlocked;
        uint256[] lockedIds;
    }
    mapping(address => LockedQueue) public lockedOf;
    mapping(address => uint256) _balances;

    /// @notice should set minter (maybe Stayking) after deployed
    function __UnbondedEvmos_init(uint256 unbondingInterval_)
        external
        initializer
    {
        __Ownable_init();
        __ERC20_init(
            'Unstaked EVMOS', // name
            'uEVMOS' //symbol
        );
        updateConfigs(unbondingInterval_);
    }

    /**************
        Modifier
     *************/
    modifier onlyMinter() {
        require(isMinter[msg.sender], 'uEVMOS: Not minter.');
        _;
    }

    function updateMinterStatus(address account, bool status)
        public
        override
        onlyOwner
    {
        isMinter[account] = status;
        emit UpdateMinterStatus(account, status);
    }

    function updateConfigs(uint256 _unbondingInterval) public onlyOwner {
        unbondingInterval = _unbondingInterval;
        emit UpdateConfigs(_unbondingInterval);
    }

    /// @return returnable EVMOS amount that user can receive
    function _repayPendingDebt(Locked storage lock)
        private
        returns (uint256 returnable)
    {
        IVault vault = IVault(lock.vault);
        address account = lock.account;
        uint256 amount = lock.amount;

        // kor) unlocked된 EVMOS 전량으로 repayPendingDebt을 갚고,
        // repayPendingDebt 함수 내에서 빚 갚고 남은 EVMOS는 다시 돌려준다.
        returnable = IVault(vault).repayPendingDebt{value: amount}(account);
        lock.received = true;
    }

    /// @dev unlock all of unlockable uEVMOS
    function _unlock(address account, LockedQueue storage lockedQueue) private {
        lockedQueue = lockedOf[account];
        uint256 nextUnlocked = lockedQueue.nextUnlocked;
        uint256 queueLength = lockedQueue.lockedIds.length;

        if (nextUnlocked >= queueLength)
            // no unlockable amounts
            return;

        uint256 unlockable;
        uint256 returnable;
        uint256 i = nextUnlocked;

        // assert under 7 loop.
        for (i; i < queueLength; i++) {
            uint256 lockedId = lockedQueue.lockedIds[i];
            Locked storage lock = locks[lockedId];

            if (lock.unlockedAt <= block.timestamp) {
                /// @dev unlockable
                unlockable += lock.amount;

                /// @dev kor) (개선 필요) aggregate하여 repay 횟수 줄이기
                uint256 returned = _repayPendingDebt(lock);
                returnable += returned;
            } else break;
        }

        if (unlockable > 0) {
            lockedQueue.nextUnlocked = i + 1; // 1. reset queue front
            _burn(account, unlockable);

            // 3. return EVMOS is returnable exists
            if (returnable > 0) {
                SafeToken.safeTransferEVMOS(account, returnable);
            }
            emit Unlock(account, unlockable, returnable);
        }
    }

    function getLockedOf(address account) public view returns (LockedQueue memory lockedQueue) {
        lockedQueue = lockedOf[account];
    }

    /******************
       Core Functions
    *******************/
    function transfer(address to, uint256 amount)
        public
        override
        onlyMinter
        returns (bool)
    {
        _transfer(msg.sender, to, amount);
        return true;
    }

    function transferFrom(
        address from,
        address to,
        uint256 amount
    ) public override onlyMinter returns (bool) {
        address spender = _msgSender();
        _spendAllowance(from, spender, amount);
        _transfer(from, to, amount);
        return true;
    }

    function lockedList(address account)
        public
        view
        returns (Locked[] memory accountLocks)
    {
        LockedQueue memory lockedQueue = lockedOf[account];
        uint256[] memory lockedIds = lockedQueue.lockedIds;

        uint256 queueLength = lockedIds.length;
        if (queueLength == 0) {
            return new Locked[](0);
        }
        uint256 nextUnlocked = lockedQueue.nextUnlocked;
        uint256 lengthOfCurrentLocked = 0;

        // 향후에 Binary Search Tree 알고리즘 형태로 바꾸면 좋을 거 같음
        for (uint256 i = queueLength - 1; i >= 0; i--) {
            if(lockedIds[i] >= nextUnlocked)
                lengthOfCurrentLocked++;
            else
                break;
        }

        accountLocks = new Locked[](lengthOfCurrentLocked);

        for (uint256 i = 0; i < lengthOfCurrentLocked; i++) {
            Locked memory locked = locks[queueLength - (1 + lengthOfCurrentLocked)];
            accountLocks[i] = locked;
        }

        // Bug 부분...
        // uint256 first = lockedQueue.nextUnlocked;
        // uint256 length = queueLength - first;
        // accountLocks = new Locked[](length);

        // for (uint256 i = 0; i < length; i++) {
        //     Locked memory locked = locks[lockedIds[i + first]];
        //     accountLocks[i] = locked;
        // }

        return accountLocks;
    }

    /// @dev mint & lock uEVMOS
    ///
    function mintLockedToken(
        address to,
        address vault,
        uint256 amount,
        uint256 debtShare
    ) public override onlyMinter {
        require(amount > 0, 'mintLockedToken: amount <= 0');

        LockedQueue storage lockedQueue = lockedOf[to];

        /// @dev consume all of unlock queue
        _unlock(to, lockedQueue);

        /// @dev limit queue size?
        // require(lockedQueue.rear - lockedQueue.front < unbondLimit, "mintLockedToken: unbond limit exceeded." );
        locks.push(
            Locked({
                account: to,
                vault: vault,
                amount: amount,
                debtShare: debtShare,
                // TODO 개선 필요
                unlockedAt: (
                    lastUnbondedAt > block.timestamp
                        ? lastUnbondedAt
                        : block.timestamp
                ) + unbondingInterval,
                received: false
            })
        );

        uint256 newLockedId = locks.length - 1;
        lockedQueue.lockedIds.push(newLockedId);

        _mint(to, amount);
        emit Lock(to, vault, newLockedId);
    }

    // unlock all because of debt.
    function unlock() public override {
        LockedQueue storage lockedQueue = lockedOf[msg.sender];
        _unlock(msg.sender, lockedQueue);
    }

    function supplyUnbondedToken() public payable override {
        lastUnbondedAt = block.timestamp;
        emit Supply(msg.value);
    }

    /// @dev calc user's unlockable uEVMOS(includes debt) & debt
    function getUnlockable(address account)
        public
        view
        override
        returns (uint256 unlockable, uint256 debt)
    {
        uint256 nextUnlocked = lockedOf[account].nextUnlocked;
        uint256 queueLength = lockedOf[account].lockedIds.length;
        uint256[] memory lockedIds = lockedOf[account].lockedIds;

        if (nextUnlocked == queueLength)
            // no unlockable amounts
            return (0, 0);

        // TODO assert under 7 loop?
        for (uint256 i = nextUnlocked; i < queueLength; i++) {
            Locked memory lock = locks[lockedIds[i]];
            if (lock.unlockedAt <= block.timestamp) {
                // unlockable amounts
                unlockable += lock.amount;
                debt += IVault(lock.vault).getPendingDebtInBase(account);
            } else break;
        }
    }

    /// @notice TODO
    /// kor) 유저가 일부만 unstake 요청하는 경우, 빚을 다 못 갚는 경우가 당연히 발생한다.
    /// 일단 부채비율이 100% 넘기는 경우 우리가 unlock 시키는 것만 구현.
    function isKillable(uint256 lockedId) public view override returns (bool) {
        Locked memory lock = locks[lockedId];
        if (lock.unlockedAt > block.timestamp) return false;
        uint256 debt = IVault(lock.vault).getPendingDebtInBase(lock.account);
        return debt >= lock.amount;
    }

    function kill(uint256 lockedId) public override {
        Locked storage lock = locks[lockedId];
        require(
            lock.unlockedAt <= block.timestamp,
            'uEVMOS Kill: Cannot Unlock.'
        );
        uint256 debt = IVault(lock.vault).getPendingDebtInBase(lock.account);
        uint256 lockedAmount = lock.amount;
        // liquidate threshold: 100%
        require(debt >= lockedAmount, 'uEVMOS Kill: Still safe.');

        IVault(lock.vault).repayInBase{value: lockedAmount}(lock.account, 1);
        lock.received = true;
        /// @dev event Kill?
    }

    receive() external payable {}

    fallback() external payable {}
}

contracts/interface/IUnbondedEvmos.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.9;

interface IUnbondedEvmos {
    function lastUnbondedAt() external view returns (uint256);

    function unbondingInterval() external view returns (uint256);

    function mintLockedToken(
        address to,
        address vault,
        uint256 amount,
        uint256 debtShare
    ) external;

    function unlock() external;

    function supplyUnbondedToken() external payable;

    function isMinter(address account) external view returns (bool);

    function updateMinterStatus(address account, bool status) external;

    function getUnlockable(address account)
        external
        view
        returns (uint256 unlockable, uint256 debt);

    function isKillable(uint256 lockedId) external returns (bool);

    function kill(uint256 lockedId) external;
}

contracts/interface/IVault.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.9;

interface IVault {
    function token() external returns (address);

    function stayking() external returns (address);

    function interestModel() external returns (address);

    function totalAmount() external view returns (uint256);

    function debtAmountOf(address user) external view returns (uint256);

    function debtAmountInBase(address user) external view returns (uint256);

    function totalStakedDebtAmount() external view returns (uint256);

    function totalDebtAmount() external view returns (uint256);

    function totalPendingDebtAmount() external view returns (uint256);

    function totalPendingDebtShare() external view returns (uint256);

    function accInterest() external view returns (uint256);

    function minReservedBps() external view returns (uint256);

    function lastAccruedAt() external view returns (uint256);

    function utilizationRateBps() external view returns (uint256);

    /// @dev denominator = 1E18
    function getInterestRate() external view returns (uint256);

    function deposit(uint256 amount) external returns (uint256 share);

    function withdraw(uint256 share) external returns (uint256 amount);

    function getPendingDebt(address user) external view returns (uint256 debt);

    function getPendingDebtInBase(address user)
        external
        view
        returns (uint256 debtInBase);

    function getBaseIn(uint256 tokenOut) external view returns (uint256 baseIn);

    function getBaseOut(uint256 tokenIn)
        external
        view
        returns (uint256 baseOut);

    function getTokenIn(uint256 baseOut)
        external
        view
        returns (uint256 tokenIn);

    function getTokenOut(uint256 baseIn)
        external
        view
        returns (uint256 tokenOut);

    function pendingDebtAmountToShare(uint256 amount)
        external
        view
        returns (uint256);

    function pendingDebtShareToAmount(uint256 share)
        external
        view
        returns (uint256);

    /******************************
     * Only for Stayking Contract *
     ******************************/
    function loan(address user, uint256 debtInBase)
        external
        returns (uint256 debt);

    function repayInToken(address user, uint256 debt) external;

    function repayInBase(address user, uint256 minRepaid)
        external
        payable
        returns (uint256 repaid);

    function takeDebtOwnership(address from, uint256 amount) external;

    function getInterestInBase() external view returns (uint256);

    function payInterest(uint256 minPaidInterest) external payable;

    function pendRepay(address user, uint256 amount)
        external
        returns (uint256 pendingDebtShare);

    function repayPendingDebt(address user) external payable returns (uint256);

    function updateInterestModel(address newInterestModel) external;

    function updateSwapHelper(address newSwapHelper) external;

    function updateMinReservedBps(uint256 newMinReservedBps) external;
}

